import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import './Signup.css';

export default function Signup() {
    const history = useNavigate();
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [name, setName] = useState('');

    const submit = async (e) => {
        e.preventDefault();

        try {
            await axios.post("http://localhost:5000/signup", { name, email, password })
                .then(res => {
                    if (res.data.status) {
                        history("/home", { state: { id: name } });
                    } else {
                        alert("Email Already exists! Please log In");
                    }
                })
                .catch(e => {
                    alert("Error");
                    console.log(e);
                });
        } catch {
            console.log(e);
        }
    }

    return (
        <section className="vh-100 gradient-custom">
            <div className="container">
                <div className="card">
                    <form action='POST' className="card-body p-5 text-center">
                        <h2 className="fw-bold mb-2 text-uppercase">UTStarcom</h2>
                        <h4 className="fw-bold mb-2 text-uppercase">Sign-Up</h4>
                        <p className="text-white-50 mb-5">Please Fill the Details!</p>
                        <div className="form-outline form-white mb-4">
                            <input
                                type="text"
                                id="name"
                                className="form-control form-control-lg"
                                placeholder='Enter your name'
                                onChange={(e) => { setName(e.target.value) }}
                                required
                            />
                            <label className="form-label" htmlFor="name">Name</label>
                        </div>
                        <div className="form-outline form-white mb-4">
                            <input
                                type="email"
                                id="signup-email"
                                className="form-control form-control-lg"
                                placeholder='Enter your email'
                                onChange={(e) => { setEmail(e.target.value) }}
                                required
                            />
                            <label className="form-label" htmlFor="signup-email">Email</label>
                        </div>
                        <div className="form-outline form-white mb-4">
                            <input
                                type="password"
                                id="signup-password"
                                className="form-control form-control-lg"
                                placeholder='Set your Password'
                                onChange={(e) => { setPassword(e.target.value) }}
                                required
                            />
                            <label className="form-label" htmlFor="signup-password">Password</label>
                        </div>
                        <input className="btn btn-outline-light btn-lg px-5" type="submit" value={'Sign Up'} onClick={submit} />
                        <p className="my-5">
                            Already have an account?{" "}
                            <Link to="/" className="text-white-50 fw-bold">Log In</Link>
                        </p>
                    </form>
                </div>
            </div>
        </section>
    )
}
