import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import './Auth.css'; // Using a common CSS file for both Login and Signup

export default function Signup() {
    const history = useNavigate();
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [name, setName] = useState('');
    const [phone, setPhone] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');

    const submit = async (e) => {
        e.preventDefault();

        if (password !== confirmPassword) {
            alert("Passwords do not match");
            return;
        }

        try {
            await axios.post("http://localhost:5000/signup", { name, email, password, phone })
                .then(res => {
                    if (res.data.status) {
                        history("/home", { state: { id: name } });
                    } else {
                        alert("Email Already exists! Please log In");
                    }
                })
                .catch(e => {
                    alert("Error");
                    console.log(e);
                });
        } catch {
            console.log(e);
        }
    }

    return (
    <div className="auth-container">
      <div className="auth-card">
        <img src="../Media/IMS LOGO.png" alt="UTStarcom" className="logo" />
        <h2>Sign Up</h2>
        <form onSubmit={submit}>
          <div className="form-group">
            <input
              type="text"
              placeholder="User Name"
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <input
              type="email"
              placeholder="E-mail Id"
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <input
              type="text"
              placeholder="Phone No."
              onChange={(e) => setPhone(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <input
              type="password"
              placeholder="Password"
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <input
              type="password"
              placeholder="Confirm Password"
              onChange={(e) => setConfirmPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="btn">Submit</button>
        </form>
        <p className="auth-switch">
          Already have an account? <Link to="/login">Login</Link>
        </p>
      </div>
    </div>
    )
}
