import React from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Navbar.css';
import logo from '../Media/IMS LOGO.png'; // Ensure the logo path is correct

export default function Navbar(props) {
  const history = useNavigate();

  const handleLogOut = async () => {
    try {
      await axios.post('http://localhost:5000/logout');
      history('/');
    } catch (error) {
      console.log(error);
    }
  };

  const handleLogoClick = () => {
    history('/dashboard', { state: { id: props.name } });
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light fixed-top custom-navbar">
      <div className="container-fluid">
        <div className="navbar-brand mt-2 mt-lg-0 logo-container" onClick={handleLogoClick} style={{ cursor: 'pointer' }}>
          <img src={logo} alt="UT Logo" />
        </div>
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav me-auto mb-2 mb-lg-0">
            <li className="nav-item">
              <NavLink className="nav-link" to="/home" activeclassname="active">
                Orders
              </NavLink>
            </li>
            <li className="nav-item">
              <NavLink className="nav-link" to="/stock" activeclassname="active">
                Stocks
              </NavLink>
            </li>
            <li className="nav-item">
              <NavLink className="nav-link" to="/service" activeclassname="active">
                Service
              </NavLink>
            </li>
          </ul>
        </div>
        <div className="d-flex align-items-center user-section">
          <span className="mx-2">User</span>
          <img
            src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQAaN1bnmFRgMyxfVfuOU3-L9jI9dL4_d_RGhi321w&s"
            className="rounded-circle"
            height={35}
            alt="User Profile"
          />
          <button className="btn btn-link logout-button" onClick={handleLogOut}>
            Logout
          </button>
        </div>
      </div>
    </nav>
  );
}
